const getHeroeByIdAsync = id => {
  const promesa = new Promise((resolve, reject) => {
    setTimeout(() => {
      // Tarea:
      // importar getHeroeById
      const heroe = getHeroeById(id);
      resolve(heroe);
    }, 2000);
  });
  return promesa;
};

getHeroeByIdAsync(3).then(heroe => console.log(heroe));
